function loadUserLoginMailingStatus()
{
	$.get('?ajax=1&action=get_user_login_mailing_status', function(data) {
 			//alert(data);
 		var response = JSON.parse(data);
 		
 		if (response.userId > 0 && response.mailingAgreement == 1)
 		{
 			//$('.guideDownloadButton').css('display','block');
 			$('tr.buttons').css('display','table-row');
 			$('#userWithoutMailingAgreement').remove();
 			$('#userLoggedOff').remove();
 		}
 		else if (response.userId == null)
 		{
 			//$('.guideDownloadButton').remove();
 			$('tr.buttons').remove();
 			$('#userWithoutMailingAgreement').remove();
 			$('#userLoggedOff').css('display','block');	
 		}
 		else if (response.mailingAgreement != 1)
 		{
 			$('tr.buttons').remove();
 			//$('.guideDownloadButton').remove();
 			$('#userWithoutMailingAgreement').css('display','block');	
 			$('#userLoggedOff').remove();	 		
 		}

	});
}

