function newest3Init() {
	$('#newest3 li div.text').each(function() {
		var html = $(this).find('*:not(:empty):last').html();
		$(this).find('*:not(:empty):last').html(html.replace(/\s*$/i, ""));
		
		var height = 0;
		$(this).children('*:not(a:has(img))').each(function() {
			height = height + $(this).outerHeight();
		})
		var maxHeight = $(this).height(); 
		var i = 0
		while (height > maxHeight && i < 200) {
			i++;
			var html = $(this).find('*:not(:empty):last').html();
			$(this).find('*:not(:empty):last').html(html.replace(/ [^ ]+( \.\.\.)?$/i, " ..."));
			
			height = 0;
			$(this).children('*:not(a:has(img))').each(function() {
				height = height + $(this).outerHeight();
			})
		}
	})
}

function autoChangeTematDniaDuzy() {
	if ($('#tematDniaDuzyMainTable').data('auto_change') && !$('#tematDniaDuzyMainTable').data('pause')) {
		var selected = $('#tematDniaDuzyMainTable tr:visible').find('.link_switch span.selected');
		if (selected) {
			var index = selected.prevAll().length;
			var count = index + selected.nextAll().length;
			//alert(index);
			if (index != count) {
				$('#tematDniaDuzyMainTable tr:visible').find('.link_switch span:eq('+(index + 1)+')').click();
			} else {
				$('#tematDniaDuzyMainTable tr:visible').find('.link_switch span:eq(0)').click();
			}
			$('#tematDniaDuzyMainTable').data('auto_change', true);
		}
	}
	setTimeout(autoChangeTematDniaDuzy, 5000);
}

